// 给定一个非空的字符串 s ，检查是否可以通过由它的一个子串重复多次构成。

// 示例 1:

// 输入: s = "abab"
// 输出: true
// 解释: 可由子串 "ab" 重复两次构成。
// 示例 2:

// 输入: s = "aba"
// 输出: false
// 示例 3:

// 输入: s = "abcabcabcabc"
// 输出: true
// 解释: 可由子串 "abc" 重复四次构成。 (或子串 "abcabc" 重复两次构成。)

// 提示：

// 1 <= s.length <= 104
// s 由小写英文字母组成
#include <string>
#include <iostream>
#include <vector>

using namespace std;
class KmpSolution
{
public:
    void getNext(int *next, const string &s)
    {
        int j = 0;
        next[0] = 0;
        for (int i = 1; i < s.size(); i++)
        {
            while (j > 0 && s[i] != s[j])
            {
                j = next[j - 1];
            }
            if (s[i] == s[j])
            {
                j++;
            }
            next[i] = j;
        }
    }
    int strStr(string haystack, string needle)
    {
        if (needle.size() == 0)
        {
            return 0;
        }
        vector<int> next(needle.size());
        getNext(&next[0], needle);
        int j = 0;
        for (int i = 0; i < haystack.size(); i++)
        {
            while (j > 0 && haystack[i] != needle[j])
            {
                j = next[j - 1];
            }
            if (haystack[i] == needle[j])
            {
                j++;
            }
            if (j == needle.size())
            {
                return (i - needle.size() + 1);
            }
        }
        return -1;
    }
};

class Solution
{
public:
    bool repeatedSubstringPattern(string s)
    {
        KmpSolution kmp = KmpSolution();
        // 创建字串
        for (int i = 0; i < s.size(); i++)
        {
            for (int j = i; j < s.size(); j++)
            {
                if (j - i > s.size() / 2)
                {
                    break;
                }

                if (s.size() % s.substr(i, j - i + 1).size() != 0 || s == s.substr(i, j - i + 1))
                {
                    continue;
                }

                for (int k = 0; k < (s.size() / s.substr(i, j - i + 1).size()); k++)
                {
                    string temps = s.substr(k * s.substr(i, j - i + 1).size());
                    if (kmp.strStr(temps, s.substr(i, j - i + 1)) != 0)
                    {
                        break;
                    }
                    if (temps.size() == s.substr(i, j - i + 1).size())
                        return true;
                }
            }
        }

        return false;
    }
};

class Solution2
{
public:
    bool repeatedSubstringPattern(string s)
    {
        KmpSolution kmp = KmpSolution();
        // 创建字串
        for (int j = 0; j < s.size()/2; j++)
        {
            if (s.size() % (j - 0 + 1) != 0)
            {
                continue;
            }

            for (int k = 0; k < (s.size() / (j - 0 + 1)); k++)
            {
                string temps = s.substr(k * (j - 0 + 1));
                if (kmp.strStr(temps, s.substr(0, j - 0 + 1)) != 0)
                {
                    break;
                }
                if (temps.size() == (j - 0 + 1))
                    return true;
            }

        }

        return false;
    }
};



class Solution3
{
public:
    bool repeatedSubstringPattern(string s)
    {
        int n = s.size();
        for (int len = 1; len <= n / 2; ++len)
        {
            // 如果长度不能整除，跳过
            if (n % len != 0)
                continue;

            // 检查子串是否重复构成原字符串
            string sub = s.substr(0, len);
            bool isValid = true;
            for (int j = len; j < n; j += len)
            {
                if (s.substr(j, len) != sub)
                {
                    isValid = false;
                    break;
                }
            }

            if (isValid)
                return true;
        }

        return false;
    }
};


int main()
{
    Solution3 s;
    string str = "";
    cout << s.repeatedSubstringPattern(str) << endl;
    return 0;
}